export const isElement = (n, a) => n.e === a

export const isInteractive = n => ['button', 'textarea', 'input', 'select', 'option'].indexOf(n.e.toLowerCase()) > -1

export const hasAttribute = (n, a) => n.m && Boolean(n.m.find(m => m.t === 13 && m.n.toLowerCase() === a))

export const hasAttributeValue = (n, a, v) => n.m && Boolean(n.m.find(m => m.t === 13 && m.n === a && m.f === v))

export const hasText = n => n.f && Boolean(n.f.find(f => typeof f === 'string'))

export const hasEvent = (n, e) => n.m && Boolean(n.m.find(m => m.t === 70 && m.n.indexOf(e) > -1))

export const isAttributeNumeric = (n, a) => !Number.isNaN(parseInt(n.m && n.m.find(m => m.t === 13 && m.n === a).f, 10))
